import { Switch, Route, NavLink } from "react-router-dom";
import './App.css';
import AddFriend from './components/AddFriend';
import FriendsList from './components/FriendList';
import LoginForm from './components/Login';
import Logout from "./components/Logout";
import AuthContextProvider, { AuthContext } from "./context/AuthContext";
import PrivateRoute from "./components/PrivateRoute";
import { useContext } from "react";

function App() {
  const {logout} = useContext(AuthContext);
  return (
    <AuthContextProvider>
      <div className="App">
        <h1>Client Auth Projesi: Friends</h1>
        <li>
          <NavLink className ="" to="/login">
            LOGİN
          </NavLink>
        </li>
        <li>
          <NavLink to="/friends">
            FRIENDS LİST
          </NavLink>
        </li>
        <li>
          <NavLink to="/friends-add">
            ADD FRIEND
          </NavLink>
        </li>
        <li>
          <NavLink to="/logout" onClick={logout} >
            LOGOUT
          </NavLink>
        </li>
        <Switch>
          <Route path="/login" component={LoginForm} />
          <PrivateRoute path="/friends" ><FriendsList/></PrivateRoute>
          <PrivateRoute path="/friends-add"><AddFriend/></PrivateRoute>
          <PrivateRoute path="/logout"><Logout/></PrivateRoute>
        </Switch>
      </div>
    </AuthContextProvider>
  );
}

export default App;
