function LoginForm() {
    const[user, setUser]= useState({
        username: "",
        password:"", 
    });

	const {
    register,
    handleSubmit,
    formState: { errors, isValid },
  } = useForm({
    mode: 'onChange',
    defaultValues: {
        username: "",
        password:"",
    }
  });

  return (
    <form onSubmit={handleSubmit()}>
      <label>
        Username
        <input
					type="text"
					name="username"
					{...register("username", { required: "İsim boş bırakılamaz" })}
				/>
      </label>
			{errors.username && <p>{errors.username.message}</p>}
      <br />

      <label>
        Password
        <input
					type="password"
					name="password"
					{...register("password", { required: "Geçerli bir şifre giriniz" })}
				/>
      </label>
			{errors.password && <p>{errors.password.message}</p>}
      <br />

      <button type="submit" disabled={!isValid}>Gönder</button>
    </form>
  );
}
  export default LoginForm;
